import React, { useEffect, useState } from "react";
import "./main.css";

const Main = () => {
    const [people, setPeople] = useState([]);

    useEffect(() => {
        // Получение данных о людях с бэкенд-сервера
        fetch('http://localhost:5000/people')
            .then((response) => response.json())
            .then((data) => setPeople(data))
            .catch((error) => console.error('Ошибка при получении данных о людях:', error));
    }, []);

    return (
        <div>
            <h2>Список людей</h2>
            <ul>
                {people.map((person) => (
                    <li key={person._id}>
                        {`Name: ${person.name}, Surname: ${person.surname}`}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Main;
