import React, { useState } from "react";
import "./navbar.css";
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();

    // Используйте состояние для отслеживания текущего класса
    const [selectedBtn, setSelectedBtn] = useState("navbar-btn");

    const MainBtn_Clicked = () => {
        navigate('*');
        // Обновите класс при клике
        setSelectedBtn("navbar-btn");
    }

    const CoursesBtn_Clicked = () => {
        navigate('/courses');
        // Обновите класс при клике
        setSelectedBtn("styles-btn");
    }
    
    return (
        <div className="navbar">
            <div className={selectedBtn} onClick={MainBtn_Clicked}>О нас</div>
            <div className={selectedBtn} onClick={CoursesBtn_Clicked}>Курсы</div>
            <div className={selectedBtn}>Галерея</div>
            <div className={selectedBtn}>Записаться</div>
            <div className={selectedBtn}>Отзывы</div>
        </div>
    );
}

export default Navbar;
