import React, { useState, useEffect } from 'react';
import "./main.css";

const Main = () => {
  const [persons, setPersons] = useState([]);

  useEffect(() => {
    // Здесь загрузите данные о людях из вашего бэкенд-сервера
    fetch('http://localhost:5000/smart/main')
      .then((response) => response.json())
      .then((data) => setPersons(data))
      .catch((error) => console.error('Ошибка при получении данных о людях:', error));
  }, []); // Пустой массив зависимостей означает, что эффект будет выполняться только при монтировании компонента

  return (
    <div>
      <h2>Список людей на главной странице</h2>
      <ul>
        {persons.map((person) => (
          <li key={person._id}>
            {`Name: ${person.name}, Surname: ${person.surname}`}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
